var Data = require('../protolus-data');
var Class = require('Classy');
var type = require('prime/util/type');
var array = require('prime/es5/array');
var fn = require('prime/es5/function');

module.exports = new Class({
    Extends : Data,
    //Implements : Data.Owner,
    initialize : function User(options){
        if(type(options) == 'string' || type(options) == 'number') options = {key:options};
        if(!options) options = {};
        options.datasource = 'mongo_ds';
        options.name = 'thing';
        this.fields = [
            '_id',
            'field',
            'worb',
            'company'
        ];
        //this.permissions = true;
        this.parent(options);
        if(options.key) this.load(options.key);
    }
});
Data.register('Thing', module.exports);