var Data = require('../protolus-data');
var Class = require('Classy');
var type = require('prime/util/type');
var array = require('prime/es5/array');
var fn = require('prime/es5/function');


var User = new Class({
    Extends : Data,
    initialize : function User(key){
        this.fields = [
            'id',
            'email',
            'password',
            'company',
            'first_name',
            'last_name',
            'phone',
            'address',
            'city',
            'state',
            'zip',
            'country'
        ];
        this.parent({
            name : 'user',
            datasource : 'database'
        });
        if(key) this.load(key);
    }
});
Data.register('User', User);
module.exports = User;